                <SelectContent>
                  <SelectItem value="inventory">تقرير المخزون</SelectItem>
                  <SelectItem value="suppliers">تقرير الموردين</SelectItem>
                  <SelectItem value="costs">تحليل التكاليف</SelectItem>
                  <SelectItem value="shipping">تقرير الشحن</SelectItem>
