  id: string;
  invoiceNumber: string;
  supplierId: string;
  items: InvoiceItem[];
  subtotal: number;
  shippingCost: number;
  customsFees: number;
  insurance: number;
  total: number;
  status: 'draft' | 'pending' | 'paid' | 'overdue';
  issueDate: Date;
  dueDate: Date;
  createdAt: Date;
  updatedAt: Date;
}

export interface InvoiceItem {
  itemId: string;
  quantity: number;
  unitPrice: number;
  total: number;
}

export interface Location {
  id: string;
  name: string;
  type: 'warehouse' | 'shelf' | 'section';
  capacity: number;
  currentStock: number;
  description?: string;
  createdAt: Date;
  updatedAt: Date;
}

export interface Shipment {
  id: string;
  shipmentNumber: string;
  billOfLading: string;
  containerNumber: string;
  status: 'in_transit' | 'arrived' | 'customs' | 'delivered';
  departureDate: Date;
  arrivalDate?: Date;
  shippingCost: number;
  customsFees: number;
  insurance: number;
