  );

  const getSupplierName = (supplierId: string) => {
    const supplier = mockSuppliers.find(s => s.id === supplierId);
    return supplier?.name || 'غير محدد';
  };

  const getLocationName = (locationId?: string) => {
    if (!locationId) return 'غير محدد';
    const location = mockLocations.find(l => l.id === locationId);
    return location?.name || 'غير محدد';
  };

  const resetForm = () => {
    setFormData({
      name: '',
      referenceNumber: '',
      type: '',
      supplierId: '',
      price: '',
      unit: '',
      specifications: '',
      quantity: '0',
      locationId: ''
    });
  };

  const handleAddItem = () => {
    setEditingItem(null);
    resetForm();
    setFormData(prev => ({
      ...prev,
      referenceNumber: AutoNumberGenerator.generateItemNumber()
    }));
    setIsDialogOpen(true);
  };

  const handleEditItem = (item: Item) => {
    setEditingItem(item);
    setFormData({
      name: item.name,
      referenceNumber: item.referenceNumber,
      type: item.type,
      supplierId: item.supplierId,
      price: item.price.toString(),
      unit: item.unit,
      specifications: item.specifications || '',
      quantity: item.quantity.toString(),
      locationId: item.locationId || ''
    });
    setIsDialogOpen(true);
  };

  const handleSaveItem = () => {
    if (!formData.name || !formData.type || !formData.supplierId || !formData.price) {
      alert('يرجى ملء جميع الحقول المطلوبة');
      return;
    }

    const newItem: Item = {
      id: editingItem?.id || Date.now().toString(),
      name: formData.name,
      referenceNumber: formData.referenceNumber,
      type: formData.type,
      supplierId: formData.supplierId,
      price: parseFloat(formData.price),
      unit: formData.unit,
      specifications: formData.specifications,
      quantity: parseInt(formData.quantity) || 0,
      locationId: formData.locationId || undefined,
      createdAt: editingItem?.createdAt || new Date()
    };

    if (editingItem) {
      setItems(items.map(item => item.id === editingItem.id ? newItem : item));
    } else {
      setItems([...items, newItem]);
    }

    setIsDialogOpen(false);
    resetForm();
  };

  const handleDeleteItem = (itemId: string) => {
    if (confirm('هل أنت متأكد من حذف هذا الصنف؟')) {
      setItems(items.filter(item => item.id !== itemId));
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">إدارة الأصناف</h1>
          <p className="text-gray-600 mt-2">إدارة جميع الأصناف والمنتجات المستوردة</p>
        </div>
        <Button onClick={handleAddItem} className="flex items-center">
          <Plus className="h-4 w-4 ml-2" />
          إضافة صنف جديد
        </Button>
      </div>

      {/* شريط البحث والفلاتر */}
      <Card>
        <CardHeader>
          <CardTitle>البحث والفلتر</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex space-x-4 space-x-reverse">
            <div className="flex-1 relative">
              <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <Input
                placeholder="البحث بالاسم أو الرقم المرجعي..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pr-10"
              />
            </div>
            <Select>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="فلتر حسب النوع" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">جميع الأنواع</SelectItem>
                <SelectItem value="industrial">معدات صناعية</SelectItem>
                <SelectItem value="spare_parts">قطع غيار</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* جدول الأصناف */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Package className="h-5 w-5 ml-2" />
            قائمة الأصناف ({filteredItems.length})
          </CardTitle>
          <CardDescription>جميع الأصناف المسجلة في النظام</CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>الاسم</TableHead>
                <TableHead>الرقم المرجعي</TableHead>
                <TableHead>النوع</TableHead>
                <TableHead>المورد</TableHead>
                <TableHead>الموقع</TableHead>
                <TableHead>الكمية</TableHead>
                <TableHead>السعر</TableHead>
                <TableHead>الإجراءات</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredItems.map((item) => (
                <TableRow key={item.id}>
                  <TableCell className="font-medium">{item.name}</TableCell>
                  <TableCell>{item.referenceNumber}</TableCell>
                  <TableCell>
                    <Badge variant="outline">{item.type}</Badge>
                  </TableCell>
                  <TableCell>{getSupplierName(item.supplierId)}</TableCell>
                  <TableCell>{getLocationName(item.locationId)}</TableCell>
                  <TableCell>
                    <span className={item.quantity < 20 ? 'text-red-600 font-semibold' : ''}>
                      {item.quantity} {item.unit}
                    </span>
                  </TableCell>
                  <TableCell>{item.price.toLocaleString('ar')} ريال</TableCell>
                  <TableCell>
                    <div className="flex space-x-2 space-x-reverse">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEditItem(item)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleDeleteItem(item.id)}
                        className="text-red-600 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* نافذة إضافة/تعديل الصنف */}
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>
              {editingItem ? 'تعديل الصنف' : 'إضافة صنف جديد'}
            </DialogTitle>
            <DialogDescription>
              {editingItem ? 'تعديل بيانات الصنف المحدد' : 'إضافة صنف جديد إلى النظام'}
            </DialogDescription>
          </DialogHeader>
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="name">اسم الصنف *</Label>
              <Input 
                id="name" 
                placeholder="أدخل اسم الصنف"
                value={formData.name}
                onChange={(e) => setFormData(prev => ({...prev, name: e.target.value}))}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="reference">الرقم المرجعي</Label>
              <Input 
                id="reference" 
                value={formData.referenceNumber}
                disabled
                className="bg-gray-50"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="type">النوع *</Label>
              <Select value={formData.type} onValueChange={(value) => setFormData(prev => ({...prev, type: value}))}>
                <SelectTrigger>
                  <SelectValue placeholder="اختر النوع" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="معدات صناعية">معدات صناعية</SelectItem>
                  <SelectItem value="قطع غيار">قطع غيار</SelectItem>
                  <SelectItem value="مواد خام">مواد خام</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="supplier">المورد *</Label>
              <Select value={formData.supplierId} onValueChange={(value) => setFormData(prev => ({...prev, supplierId: value}))}>
                <SelectTrigger>
                  <SelectValue placeholder="اختر المورد" />
                </SelectTrigger>
                <SelectContent>
                  {mockSuppliers.map((supplier) => (
                    <SelectItem key={supplier.id} value={supplier.id}>
                      {supplier.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="price">السعر *</Label>
              <Input 
                id="price" 
                type="number" 
                placeholder="أدخل السعر"
                value={formData.price}
                onChange={(e) => setFormData(prev => ({...prev, price: e.target.value}))}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="unit">وحدة القياس *</Label>
              <Select value={formData.unit} onValueChange={(value) => setFormData(prev => ({...prev, unit: value}))}>
                <SelectTrigger>
                  <SelectValue placeholder="اختر الوحدة" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="قطعة">قطعة</SelectItem>
                  <SelectItem value="كيلوجرام">كيلوجرام</SelectItem>
                  <SelectItem value="لتر">لتر</SelectItem>
                  <SelectItem value="متر">متر</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="quantity">الكمية</Label>
              <Input 
                id="quantity" 
                type="number" 
                placeholder="أدخل الكمية"
                value={formData.quantity}
                onChange={(e) => setFormData(prev => ({...prev, quantity: e.target.value}))}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="location">الموقع</Label>
              <Select value={formData.locationId} onValueChange={(value) => setFormData(prev => ({...prev, locationId: value}))}>
                <SelectTrigger>
                  <SelectValue placeholder="اختر الموقع" />
                </SelectTrigger>
                <SelectContent>
                  {mockLocations.map((location) => (
                    <SelectItem key={location.id} value={location.id}>
                      {location.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="col-span-2 space-y-2">
              <Label htmlFor="specifications">المواصفات</Label>
              <Textarea 
                id="specifications" 
                placeholder="أدخل مواصفات الصنف"
                value={formData.specifications}
                onChange={(e) => setFormData(prev => ({...prev, specifications: e.target.value}))}
              />
            </div>
          </div>
          <div className="flex justify-end space-x-2 space-x-reverse mt-4">
            <Button variant="outline" onClick={() => setIsDialogOpen(false)}>
              إلغاء
            </Button>
            <Button onClick={handleSaveItem}>
              {editingItem ? 'حفظ التعديل' : 'إضافة الصنف'}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}
</content>
